    const REFRESH_URL = 'http://localhost:4000/refresh';
    const [isLoading, setIsLoading] = useState(true);
    const { auth, setAuth, rememberUser } = useAuth();

    useEffect(() => {
        const refreshToken = { refreshToken: auth.refreshToken }
