import { createContext, useContext, useState } from 'react';
import useLocalStorage from '../hooks/useLocalStorage';
import axios from 'axios';

const AuthContext = createContext();

export function useAuth() {
    return useContext(AuthContext);
}

export function useRefreshToken() {
    const REFRESH_URL = 'http://localhost:4000/refresh';
    const { auth, setAuth } = useAuth();
    const accessToken = { refreshToken: auth.accessToken };
    axios.get(REFRESH_URL, accessToken).then(res => {
        setAuth(prev => {
            return {...prev, accessToken: res.data.accessToken}
        });
    });
}

export function AuthProvider({ children }) {
    const [auth, setAuth] = useLocalStorage('user', []);
    const [rememberUser, setRememberUser] = useLocalStorage('remember-user', false);

    return (
        <AuthContext.Provider value={{ auth, setAuth, rememberUser, setRememberUser }}>
            {children}
        </AuthContext.Provider>
    )
}
