        conversations.forEach((conversation, convoIdx) => {
            if (convoIdx === idx) {
                conversation.classList.add('bg-gray-200');
            } else {
                conversation.classList.remove('bg-gray-200');
            }
        });

        setSelectedConversationIdx(idx);
    };

